            },
        },
    ]

    response = hl.chat(
        project="Assistant",
        model_config={"model": "gpt-3.5-turbo-1106", "tools": tools, "max_tokens": 100},
        messages=messages,
    )
    response = response.body
    output_message = response["data"][0]["output_message"]

