)

func main() {
    client := polytomicgoclient.NewClient(polytomic.WithToken(os.Getenv("POLYTOMIC_API_TOKEN")))

    query := `
SELECT Account__c, COUNT(ID) num_active_ FROM Business_Unit__c
WHERE Status__c = 'Active' AND Subscription_Type__c = 'Self Service'
GROUP BY Account__c
`

    resp, err := client.QueryRunner.RunQuery(context.TODO(), &polytomic.RunQueryRequest{
        ConnectionId: "SALESFORCE-CONNECTION-ID",
        Query:        query,
    })

    if err != nil {
        fmt.Println("Error running query:", err)
        return
    }

    fmt.Println("Query Results:", resp.Data)
}

