    method: "POST",
    mode: "cors",
    cache: "no-cache",
    credentials: "same-origin",
    body: JSON.stringify({
      whitelist: ["salesforce", "hubspot", "marketo", "outreach"],
      name: "Polytomic connection",
      redirect_url: REDIRECT_URL,
    }),
  });
  if (response.ok) {
    const data = await response.json();
    window.open(data.data.redirect_url, "_blank")
    return;
