    def category(self):
        return self._category

    @property
    def description(self):
        return self._description

@classmethod
def from_string(cls, category: str) -> "FinancialCategory":
    for c in cls:
        if c.category == category:
            return c
    raise ValueError(f"Invalid category: {category}")

